                os.close();
            }
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            // Ignore
        } finally {
            connected = false;
        }
    }

    public void flush() throws IOException {
        os.flush();
    }

    public boolean isConnected() {
