    private void tryAllAddresses(Socket sock, String host, int port) throws SocketTimeoutException {
        try {
            sock.connect(new InetSocketAddress(host, port), 5000);
        } catch (IOException e) {
            throw new SocketTimeoutException("Connection timed out");
