import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.net.Socket;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import org.apache.logging.log4j.LogManager;
import org.apache.logging.log4j.Logger;

public class NetworkForwarder {
    private static final Logger LOGGER = LogManager.getLogger(NetworkForwarder.class);

    private final InetAddress destination;
    private final DatagramSocket connectionUdp;
    private final Map<InetSocketAddress, Socket> connectionsTcp;

    public NetworkForwarder(Config config) throws IOException {
        this.destination = InetAddress.getByName(config.getDestination());
        this.connectionUdp = new DatagramSocket(config.getUdpPort());
        this.connectionsTcp = new ConcurrentHashMap<>();
    }

    public void forward(InetSocketAddress source, int port, boolean datagram, byte[] data) {
        try {
            if (datagram) {
                DatagramPacket packet = new DatagramPacket(data, data.length, destination, port);
                connectionUdp.send(packet);
            } else {
                Socket socket = connectionsTcp.computeIfAbsent(source, k -> {
                    try {
                        return new Socket(source.getAddress(), port);
                    } catch (IOException e) {
                        LOGGER.warn("Failed to create TCP connection for source: {}", source, e);
                        return null;
                    }
                });

                if (socket != null) {
                    socket.getOutputStream().write(data);
                }
            }
        } catch (IOException e) {
            LOGGER.error("Failed to forward data to destination: {}", destination, e);
        }
    }

    public void disconnect(InetSocketAddress source) {
        Socket socket = connectionsTcp.remove(source);
        if (socket != null) {
            try {
                socket.close();
            } catch (IOException e) {
                LOGGER.warn("Failed to close TCP connection for source: {}", source, e);
            }
        }
    }
