import io.netty.buffer.ByteBuf;
import io.netty.buffer.Unpooled;
import io.netty.util.internal.logging.InternalLogger;
import io.netty.util.internal.logging.InternalLoggerFactory;

import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.ObjectOutputStream;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;
import java.util.Map;
import java.util.concurrent.ExecutorService;

public class PositionForwarderWialon {
    private static final InternalLogger log = InternalLoggerFactory.getInstance(PositionForwarderWialon.class);

    private DatagramSocket socket;
    private InetAddress address;
    private int port;

    public PositionForwarderWialon(Config config, ExecutorService executorService) {
        try {
            this.socket = new DatagramSocket();
            this.address = InetAddress.getByName(config.getServerAddress());
            this.port = config.getServerPort();
        } catch (SocketException | UnknownHostException e) {
            log.error("Failed to initialize UDP socket", e);
        }
    }

    public void sendPosition(Position position, String uniqueId, ResultHandler resultHandler) {
        String attributes = formatAttributes(position.getAttributes());
        String message = String.format("id=%s;lat=%s;lon=%s;alt=%s;spd=%s;dir=%s;flags=%s;type=%s;attributes=%s",
                uniqueId, position.getLatitude(), position.getLongitude(), position.getAltitude(),
                position.getSpeed(), position.getDirection(), position.getFlags(), position.getType(), attributes);

        byte[] data = message.getBytes();
        ByteBuf compressedData = compressData(data);

        DatagramPacket packet = new DatagramPacket(compressedData.array(), compressedData.array().length, address, port);
        socket.send(packet);

        resultHandler.onResult(true, null);
    }

    private static ByteBuf compressData(byte[] data) {
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
        try (ObjectOutputStream objectOutputStream = new ObjectOutputStream(byteArrayOutputStream)) {
            objectOutputStream.writeObject(data);
        } catch (IOException e) {
            log.error("Failed to compress data", e);
        }
        return Unpooled.wrappedBuffer(byteArrayOutputStream.toByteArray());
    }

    private static String formatAttributes(Map<String, Object> attributes) {
        StringBuilder sb = new StringBuilder();
        for (Map.Entry<String, Object> entry : attributes.entrySet()) {
            sb.append(entry.getKey()).append("=").append(entry.getValue()).append(";");
        }
        return sb.toString();
    }

    public interface ResultHandler {
        void onResult(boolean success, Throwable throwable);
    }
}
