import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketAddress;
import java.net.SocketException;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.atomic.AtomicBoolean;

public class UdpTransport {
    private DatagramSocket sock;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server;
    private Log log;
    private List<SocketAddress> members;

    public UdpTransport() {
        members = new ArrayList<>();
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public Object localAddress() {
        return sock.getLocalSocketAddress();
    }

    public List<? extends Object> clusterMembers() {
        return members;
    }

    public void start(String... options) {
        for (String option : options) {
            if (option.equals("server")) {
                server = true;
            }
        }
        try {
            if (server) {
                sock = new DatagramSocket(port);
            } else {
                sock = new DatagramSocket();
            }
            receiver_thread = new Receiver();
            receiver_thread.start();
        } catch (SocketException e) {
            log.error("Failed to start UDP transport: " + e.getMessage());
        }
    }

    public void stop() {
        receiver_thread.stop();
        sock.close();
    }

    public void send(Object destination, byte[] data, int offset, int length) {
        try {
            DatagramPacket packet = new DatagramPacket(data, offset, length, (SocketAddress) destination);
            sock.send(packet);
        } catch (IOException e) {
            log.error("Failed to send UDP packet: " + e.getMessage());
        }
    }

    private class Receiver extends Thread {
        private AtomicBoolean running = new AtomicBoolean(true);

        public void run() {
            byte[] buffer = new byte[65535];
            DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
            while (running.get()) {
                try {
                    sock.receive(packet);
                    if (receiver != null) {
                        receiver.receive(packet.getData(), packet.getLength(), packet.getSocketAddress());
                    }
                } catch (IOException e) {
                    log.error("Failed to receive UDP packet: " + e.getMessage());
                }
            }
        }

        public void stop() {
            running.set(false);
        }
    }
}
