import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;
import java.util.Arrays;

public class SntpImpl implements Sntp {

    private static final int NTP_PORT = 123;
    private static final int NTP_MODE = 3;
    private static final int NTP_VERSION = 3;
    private static final int NTP_PACKET_SIZE = 48;
    private static final long OFFSET_1900_TO_1970 = 2208988800L;

    @Override
    public SntpResult requestTime(InetAddress serverAddress, float timeout, float pollInterval, int maxPolls, int version, SntpEventListener listener) {
        DatagramSocket socket = null;
        try {
            socket = new DatagramSocket();
            byte[] request = new byte[NTP_PACKET_SIZE];
            writeNtpVersion(request);
            writeTimeStamp(request, 40, System.currentTimeMillis() / 1000L + OFFSET_1900_TO_1970);

            DatagramPacket packet = new DatagramPacket(request, request.length, serverAddress, NTP_PORT);
            socket.send(packet);

            byte[] response = new byte[NTP_PACKET_SIZE];
            packet = new DatagramPacket(response, response.length);
            socket.setSoTimeout((int) (timeout * 1000));
            socket.receive(packet);

            long originateTimestamp = readTimeStamp(request, 40);
            long receiveTimestamp = readTimeStamp(response, 24);
            long transmitTimestamp = readTimeStamp(response, 40);

            long roundTripTime = (transmitTimestamp - originateTimestamp);
            long clockOffset = ((receiveTimestamp - originateTimestamp) - (transmitTimestamp - originateTimestamp)) / 2;

            if (listener != null) {
                listener.onTimeResponse(clockOffset, roundTripTime);
            }

            return new SntpResult(clockOffset, roundTripTime);
        } catch (SocketException e) {
            if (listener != null) {
                listener.onError(e);
            }
            return new SntpResult(e);
        } catch (IOException e) {
            if (listener != null) {
                listener.onError(e);
            }
            return new SntpResult(e);
        } finally {
            if (socket != null) {
                socket.close();
            }
        }
    }

    private void writeNtpVersion(byte[] buffer) {
        buffer[0] = (byte) ((NTP_VERSION << 3) | NTP_MODE);
    }

    private void writeTimeStamp(byte[] buffer, int offset, long timestamp) {
        long seconds = timestamp;
        buffer[offset] = (byte) (seconds >> 24);
        buffer[offset + 1] = (byte) (seconds >> 16);
        buffer[offset + 2] = (byte) (seconds >> 8);
        buffer[offset + 3] = (byte) (seconds);

        long fraction = (timestamp * 0x100000000L) % 0x100000000L;
        buffer[offset + 4] = (byte) (fraction >> 24);
        buffer[offset + 5] = (byte) (fraction >> 16);
        buffer[offset + 6] = (byte) (fraction >> 8);
        buffer[offset + 7] = (byte) (fraction);
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        long seconds = read(buffer, offset);
        long fraction = read(buffer, offset + 4);
        return seconds * 1000L + (fraction * 1000L >> 32);
    }

    private long read(byte[] buffer, int offset) {
        return ((buffer[offset] & 0xff) << 24) | ((buffer[offset + 1] & 0xff) << 16) | ((buffer[offset + 2] & 0xff) << 8) | (buffer[offset + 3] & 0xff);
    }

    private int ui(byte b) {
        return b & 0xff;
    }

    private double doubleMillis(long fixedPoint) {
        return (fixedPoint * 1000L) / 0x100000000L;
    }
}
