import java.io.OutputStream;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;

public class DropwizardUdpSocketAppender extends OutputStreamAppender {

    private String host;
    private int port;

    public DropwizardUdpSocketAppender(String host, int port) {
        this.host = host;
        this.port = port;
    }

    @Override
    public void start() {
        super.start();
    }

    @Override
    protected OutputStream datagramSocketOutputStream(String host, int port) {
        try {
            DatagramSocket socket = new DatagramSocket();
            InetAddress address = InetAddress.getByName(host);
            return new UdpOutputStream(socket, address, port);
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    private class UdpOutputStream extends OutputStream {

        private DatagramSocket socket;
        private InetAddress address;
        private int port;

        public UdpOutputStream(DatagramSocket socket, InetAddress address, int port) {
            this.socket = socket;
            this.address = address;
            this.port = port;
        }

        @Override
        public void write(int b) {
            throw new UnsupportedOperationException("UDP does not support byte-level writes");
        }

        @Override
        public void write(byte[] b, int off, int len) {
            try {
                DatagramPacket packet = new DatagramPacket(b, len, address, port);
                socket.send(packet);
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }

        @Override
        public void close() {
            socket.close();
        }
    }
}
