public class SingleClientUdpTracker {
    private static final Duration CONNECTION_EXPIRATION_TIME = Duration.ofMinutes(10);
    private static final long EPHEMERAL_CONNECTION_ID = 0x41727101980L;
    private static final int CONNECT_CODE = 0;
    private static final int ANNOUNCE_CODE = 1;
    private static final int ERROR_CODE = 3;

    private DatagramSocket serverSocket;
    private int interval;
    private int leechers;
    private int seeders;
    private boolean shutdown;
    private long currentConnection;
    private long connectedOn;

    public SingleClientUdpTracker(int interval, int leechers, int seeders) throws SocketException {
        this.serverSocket = new DatagramSocket();
        this.interval = interval;
        this.leechers = leechers;
        this.seeders = seeders;
        this.shutdown = false;
        this.currentConnection = 0;
        this.connectedOn = 0;
    }

    public SocketAddress getServerAddress() {
        return serverSocket.getLocalSocketAddress();
    }

    public void run() {
        while (!shutdown) {
            byte[] buffer = new byte[1024];
            DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
            try {
                serverSocket.receive(packet);
                byte[] response = processRequest(packet.getData());
                if (response != null) {
                    DatagramPacket responsePacket = new DatagramPacket(response, response.length, packet.getSocketAddress());
                    serverSocket.send(responsePacket);
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
        serverSocket.close();
    }

    private byte[] processRequest(byte[] requestData) {
        int action = ((requestData[0] & 0xFF) << 24) | ((requestData[1] & 0xFF) << 16) | ((requestData[2] & 0xFF) << 8) | (requestData[3] & 0xFF);
        int transactionId = ((requestData[4] & 0xFF) << 24) | ((requestData[5] & 0xFF) << 16) | ((requestData[6] & 0xFF) << 8) | (requestData[7] & 0xFF);
        switch (action) {
            case CONNECT_CODE:
                return createConnectResponse(generateConnectionId(), transactionId);
            case ANNOUNCE_CODE:
                if (currentConnection == 0 || System.currentTimeMillis() - connectedOn > CONNECTION_EXPIRATION_TIME.toMillis()) {
                    return createError(transactionId, "Invalid connection");
                }
                return createAnnounceResponse(currentConnection, transactionId);
            default:
                return createError(transactionId, "Invalid action");
        }
    }

    private long generateConnectionId() {
        return EPHEMERAL_CONNECTION_ID;
    }

    private byte[] createConnectResponse(long connectionId, int transactionId) {
        byte[] response = new byte[16];
        response[0] = (byte) (connectionId >> 24);
        response[1] = (byte) (connectionId >> 16);
        response[2] = (byte) (connectionId >> 8);
        response[3] = (byte) (connectionId);
        response[4] = (byte) (transactionId >> 24);
        response[5] = (byte) (transactionId >> 16);
        response[6] = (byte) (transactionId >> 8);
        response[7] = (byte) (transactionId);
        return response;
    }

    private byte[] createAnnounceResponse(long connectionId, int transactionId) {
        byte[] response = new byte[20];
        response[0] = (byte) (connectionId >> 24);
        response[1] = (byte) (connectionId >> 16);
        response[2] = (byte) (connectionId >> 8);
        response[3] = (byte) (connectionId);
        response[4] = (byte) (transactionId >> 24);
        response[5] = (byte) (transactionId >> 16);
        response[6] = (byte) (transactionId >> 8);
        response[7] = (byte) (transactionId);
        response[8] = (byte) (interval >> 24);
        response[9] = (byte) (interval >> 16);
        response[10] = (byte) (interval >> 8);
        response[11] = (byte) (interval);
        response[12] = (byte) (leechers >> 24);
        response[13] = (byte) (leechers >> 16);
        response[14] = (byte) (leechers >> 8);
        response[15] = (byte) (leechers);
        response[16] = (byte) (seeders >> 24);
        response[17] = (byte) (seeders >> 16);
        response[18] = (byte) (seeders >> 8);
        response[19] = (byte) (seeders);
        return response;
    }

    private byte[] createError(int transactionId, String message) {
        byte[] response = new byte[8 + message.length()];
        response[0] = (byte) (ERROR_CODE >> 24);
        response[1] = (byte) (ERROR_CODE >> 16);
        response[2] = (byte) (ERROR_CODE >> 8);
        response[3] = (byte) (ERROR_CODE);
        response[4] = (byte) (transactionId >> 24);
        response[5] = (byte) (transactionId >> 16);
        response[6] = (byte) (transactionId >> 8);
        response[7] = (byte) (transactionId);
        for (int i = 0; i < message.length(); i++) {
            response[8 + i] = (byte) message.charAt(i);
        }
        return response;
    }

    public void shutdown() {
        shutdown = true;
    }
