import java.io.IOException;
import java.net.*;
import java.util.ArrayList;
import java.util.List;

public class DnsUdpClient {

    private static final InetSocketAddress[] DNS_SERVERS = {
            new InetSocketAddress("8.8.8.8", 53),
            new InetSocketAddress("8.8.4.4", 53)
    };

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        DatagramPacket request = new DatagramPacket(query, query.length, dnsServer);
        socket.send(request);

        byte[] buffer = new byte[512];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);

        return toIps(buffer);
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        DatagramPacket request = new DatagramPacket(query, query.length, dnsServer);
        socket.send(request);

        byte[] buffer = new byte[512];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);

        return toTXT(buffer);
    }

    private List<Inet4Address> toIps(byte[] buffer) {
        List<Inet4Address> ips = new ArrayList<>();
        int offset = 12;
        while (offset < buffer.length) {
            int type = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            if (type == 1) {
                int length = buffer[offset + 10] & 0xFF;
                byte[] ipBytes = new byte[length];
                System.arraycopy(buffer, offset + 12, ipBytes, 0, length);
                try {
                    ips.add((Inet4Address) InetAddress.getByAddress(ipBytes));
                } catch (UnknownHostException e) {
                    e.printStackTrace();
                }
            }
            offset += 12 + length;
        }
        return ips;
    }

    private String toTXT(byte[] buffer) {
        int offset = 12;
        while (offset < buffer.length) {
            int type = ((buffer[offset] & 0xFF) << 8) | (buffer[offset + 1] & 0xFF);
            if (type == 16) {
                int length = buffer[offset + 10] & 0xFF;
                byte[] txtBytes = new byte[length];
                System.arraycopy(buffer, offset + 12, txtBytes, 0, length);
                return new String(txtBytes);
            }
            offset += 12 + length;
        }
        throw new RuntimeException("TXT record not found");
    }
}
