    private String env;
    private OnRequestCallback callback;

    public HttpFeedbackClient(String content, String env, OnRequestCallback callback) {
        this.content = content;
        this.env = env;
        this.callback = callback;
    }

    public void sendFeedback() {
        try {
            String urlParameters = "content=" + URLEncoder.encode(content, "UTF-8") +
                    "&env=" + URLEncoder.encode(env, "UTF-8");

            URL url = new URL("http://example.com/feedback");
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
            connection.setRequestProperty("Content-Length", "" + Integer.toString(urlParameters.getBytes().length));
            connection.setRequestProperty("Content-Language", "en-US");
            connection.setUseCaches(false);
            connection.setDoInput(true);
            connection.setDoOutput(true);

            DataOutputStream wr = new DataOutputStream(connection.getOutputStream());
            wr.writeBytes(urlParameters);
            wr.flush();
            wr.close();

            BufferedReader rd = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            String line;
            StringBuilder response = new StringBuilder();
            while ((line = rd.readLine()) != null) {
                response.append(line);
                response.append('\r');
            }
            rd.close();

            if (connection.getResponseCode() == HttpURLConnection.HTTP_OK) {
                callback.onSuccess(response.toString());
            } else {
                callback.onFailure(new Exception("HTTP request failed with code: " + connection.getResponseCode()));
            }

            connection.disconnect();
        } catch (Exception e) {
            callback.onFailure(e);
        }
    }

    public interface OnRequestCallback {
        void onSuccess(String response);
        void onFailure(Exception e);
    }
