import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpRequestHandler {

    private static final String TAG = "HttpRequestHandler";

    private class MyAsyncTaskgetNews extends AsyncTask<String, String, String> {

        @Override
        protected String doInBackground(String... params) {
            String urlString = params[0];
            HttpURLConnection urlConnection = null;
            try {
                URL url = new URL(urlString);
                urlConnection = (HttpURLConnection) url.openConnection();
                urlConnection.setConnectTimeout(7000);
                InputStream inputStream = urlConnection.getInputStream();
                String result = convertStreamToString(inputStream);
                publishProgress(result);
                return null;
            } catch (Exception e) {
                Log.e(TAG, "Error: " + e.getMessage());
            } finally {
                if (urlConnection != null) {
                    urlConnection.disconnect();
                }
            }
            return null;
        }

        @Override
        protected void onProgressUpdate(String... progress) {
            super.onProgressUpdate(progress);
            try {
                JSONObject jsonObject = new JSONObject(progress[0]);
                String status = jsonObject.getString("status");
                if ("user is added".equals(status)) {
                    Toast.makeText(getApplicationContext(), "User is added", Toast.LENGTH_SHORT).show();
                    // Initiate login
                } else if ("success".equals(status)) {
                    // Save user credentials
                    SaveSettings.saveUserCredentials(getApplicationContext(), jsonObject.getString("username"), jsonObject.getString("password"));
                } else {
                    Log.e(TAG, "Error: " + jsonObject.getString("error"));
                }
            } catch (JSONException e) {
                Log.e(TAG, "Error parsing JSON: " + e.getMessage());
            }
        }

        private String convertStreamToString(InputStream inputStream) throws IOException {
            BufferedReader bufferedReader = new BufferedReader(new InputStreamReader(inputStream));
            StringBuilder stringBuilder = new StringBuilder();
            String line;
            while ((line = bufferedReader.readLine()) != null) {
                stringBuilder.append(line).append("\n");
            }
            bufferedReader.close();
            return stringBuilder.toString();
        }
    }
}
