    public static String httpRequest(SimpleHttpParam httpParam) {
        String result = null;
        HttpURLConnection urlConn = null;
        try {
            urlConn = (HttpURLConnection) new URL(httpParam.getUrl()).openConnection();
            urlConn.setConnectTimeout(httpParam.getConnectTimeout());
            urlConn.setReadTimeout(httpParam.getReadTimeout());
            urlConn.setRequestMethod(httpParam.getMethod());
            urlConn.setDoOutput(true);
            urlConn.setDoInput(true);
            urlConn.setUseCaches(false);
            urlConn.setInstanceFollowRedirects(true);
            urlConn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
            if (httpParam.getHeaders() != null) {
                for (Entry<String, String> entry : httpParam.getHeaders().entrySet()) {
                    urlConn.setRequestProperty(entry.getKey(), entry.getValue());
                }
            }
            if (httpParam.getMethod().equalsIgnoreCase(HTTP_METHOD_POST)) {
                String paramStr = mapToQueryString(httpParam.getParams(), httpParam.getCharSet());
                urlConn.getOutputStream().write(paramStr.getBytes(httpParam.getCharSet()));
            }
            if (urlConn.getResponseCode() == 200) {
                result = readResponse(urlConn.getInputStream(), httpParam.getCharSet());
            } else {
                result = readResponse(urlConn.getErrorStream(), httpParam.getCharSet());
            }
        } catch (Exception e) {
            logger.error("Error occurred while making HTTP request", e);
        } finally {
            if (urlConn != null) {
                urlConn.disconnect();
            }
        }
        return result;
    }

    public static String mapToQueryString(Map parameters, String charSet) {
        StringBuilder queryString = new StringBuilder();
        if (parameters != null) {
            for (Entry<String, Object> entry : parameters.entrySet()) {
                Object value = entry.getValue();
                if (value != null) {
                    List<String> values = makeStringList(value);
                    for (String v : values) {
                        if (queryString.length() > 0) {
                            queryString.append("&");
                        }
                        queryString.append(urlEncode(entry.getKey(), charSet)).append("=").append(urlEncode(v, charSet));
                    }
                }
            }
        }
        return queryString.toString();
    }

    public static String urlEncode(String url, String charSet) {
        try {
            return URLEncoder.encode(url, charSet);
        } catch (Exception e) {
            logger.error("Error occurred while URL encoding", e);
        }
        return url;
    }

    public static void setSSLSocketFactory(HttpURLConnection urlConn, boolean sslVerify, boolean hostnameVerify, TrustKeyStore trustKeyStore, ClientKeyStore clientKeyStore) {
        try {
            SSLContext sslContext = SSLContext.getInstance("TLS");
            sslContext.init(clientKeyStore != null ? clientKeyStore.getKeyManagers() : null, sslVerify ? trustAnyManagers : trustKeyStore != null ? trustKeyStore.getTrustManagers() : null, new SecureRandom());
            if (urlConn instanceof HttpsURLConnection) {
                HttpsURLConnection httpsConn = (HttpsURLConnection) urlConn;
                httpsConn.setSSLSocketFactory(sslContext.getSocketFactory());
                if (!hostnameVerify) {
                    httpsConn.setHostnameVerifier(new TrustAnyHostnameVerifier());
                }
            }
        } catch (Exception e) {
            logger.error("Error occurred while setting SSL socket factory", e);
        }
    }

    public static List<String> makeStringList(Object value) {
        List<String> values = new ArrayList<>();
        if (value instanceof List) {
            for (Object v : (List) value) {
                values.add(v.toString());
            }
        } else {
            values.add(value.toString());
        }
        return values;
    }

    private static String readResponse(InputStream inputStream, String charSet) throws IOException {
        StringBuilder result = new StringBuilder();
        BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream, charSet));
        String line;
        while ((line = reader.readLine()) != null) {
            result.append(line);
        }
        reader.close();
        return result.toString();
    }

    private static class TrustAnyTrustManager implements X509TrustManager {

        @Override
        public void checkClientTrusted(X509Certificate[] chain, String authType) throws CertificateException {
        }

