import android.content.Context;
import android.util.Log;
import com.squareup.okhttp.ConnectionPool;
import com.squareup.okhttp.OkHttpClient;
import com.squareup.okhttp.Request;
import com.squareup.okhttp.Response;
import java.io.IOException;
import java.net.HttpURLConnection;
import java.net.MalformedURLException;
import java.net.Proxy;
import java.net.URL;
import java.util.List;
import java.util.Locale;
import java.util.Map;

public class MmsHttpClient {
    private static final String TAG = "MmsHttpClient";
    private final Context mContext;
    private final SocketFactory mSocketFactory;
    private final MmsNetworkManager mHostResolver;
    private final ConnectionPool mConnectionPool;

    public MmsHttpClient(Context context, SocketFactory socketFactory, MmsNetworkManager hostResolver, ConnectionPool connectionPool) {
        mContext = context;
        mSocketFactory = socketFactory;
        mHostResolver = hostResolver;
        mConnectionPool = connectionPool;
    }

    public byte[] execute(String urlString, byte[] pdu, String method, boolean isProxySet, String proxyHost, int proxyPort, MmsConfig.Overridden mmsConfig) throws IOException {
        checkMethod(method);
        URL url = new URL(urlString);
        Proxy proxy = isProxySet ? new Proxy(Proxy.Type.HTTP, new InetSocketAddress(proxyHost, proxyPort)) : Proxy.NO_PROXY;
        HttpURLConnection connection = openConnection(url, proxy);
        addExtraHeaders(connection, mmsConfig);
        if ("POST".equals(method)) {
            connection.setDoOutput(true);
            connection.getOutputStream().write(pdu);
        }
        return getResponse(connection);
    }

    private HttpURLConnection openConnection(URL url, Proxy proxy) throws MalformedURLException {
        OkHttpClient client = new OkHttpClient();
        client.setSocketFactory(mSocketFactory);
        client.setHostnameVerifier((hostname, session) -> true);
        client.setConnectionPool(mConnectionPool);
        client.setProxy(proxy);
        Request request = new Request.Builder().url(url).build();
        try {
            return client.open(request);
        } catch (IOException e) {
            throw new MalformedURLException("Invalid protocol: " + url.getProtocol());
        }
    }

    private void checkMethod(String method) {
        if (!"GET".equals(method) && !"POST".equals(method)) {
            throw new IllegalArgumentException("Invalid HTTP method: " + method);
        }
    }

    private String getCurrentAcceptLanguage(Locale locale) {
        return locale.getLanguage() + "-" + locale.getCountry();
    }

    private void addExtraHeaders(HttpURLConnection connection, MmsConfig.Overridden mmsConfig) {
        connection.setRequestProperty("Accept-Language", getCurrentAcceptLanguage(Locale.getDefault()));
        connection.setRequestProperty("Accept", "application/vnd.wap.mms-message");
        connection.setRequestProperty("User-Agent", "Android-Mms/2.0");
        connection.setRequestProperty("Accept-Encoding", "gzip");
        connection.setRequestProperty("Connection", "close");
        if (mmsConfig != null) {
            connection.setRequestProperty("X-Mms-Message-Type", String.valueOf(mmsConfig.getMessageType()));
            connection.setRequestProperty("X-Mms-Message-Class", mmsConfig.getMessageClass().toString());
            connection.setRequestProperty("X-Mms-Delivery-Report", String.valueOf(mmsConfig.getDeliveryReport()));
            connection.setRequestProperty("X-Mms-Read-Report", String.valueOf(mmsConfig.getReadReport()));
        }
    }

    private byte[] getResponse(HttpURLConnection connection) throws IOException {
        try {
            Map<String, List<String>> headers = connection.getHeaderFields();
            logHttpHeaders(headers);
            return connection.getInputStream().readAllBytes();
        } finally {
            connection.disconnect();
        }
    }

    private void logHttpHeaders(Map<String, List<String>> headers) {
        for (Map.Entry<String, List<String>> entry : headers.entrySet()) {
            Log.d(TAG, entry.getKey() + ": " + entry.getValue());
        }
    }
}
