    public HttpUrlSource(String url, HostnameVerifier v, TrustManager[] trustAllCerts) {
        this(url, null, v, trustAllCerts);
    }

    public HttpUrlSource(String url, SourceInfoStorage sourceInfoStorage, HostnameVerifier v, TrustManager[] trustAllCerts) {
        this(url, sourceInfoStorage, new HeaderInjector(), v, trustAllCerts);
    }

    public HttpUrlSource(String url, SourceInfoStorage sourceInfoStorage, HeaderInjector headerInjector, HostnameVerifier v, TrustManager[] trustAllCerts) {
        this.sourceInfoStorage = sourceInfoStorage;
        this.headerInjector = headerInjector;
        this.v = v;
        this.trustAllCerts = trustAllCerts;
        this.sourceInfo = new SourceInfo(url);
    }

    public long length() {
        if (sourceInfo.getLength() == -1) {
            fetchContentInfo();
        }
        return sourceInfo.getLength();
    }

    public void open(long offset) {
        if (connection != null) {
            close();
        }
        connection = openConnection(offset, 0);
        inputStream = connection.getInputStream();
    }

    public void close() {
        if (inputStream != null) {
            try {
                inputStream.close();
            } catch (Exception e) {
                // ignore
            }
            inputStream = null;
        }
        if (connection != null) {
            connection.disconnect();
            connection = null;
        }
    }

    public int read(byte[] buffer) {
        try {
            return inputStream.read(buffer);
        } catch (Exception e) {
            return -1;
        }
    }

    public String getMime() {
        if (sourceInfo.getMime() == null) {
            fetchContentInfo();
        }
        return sourceInfo.getMime();
    }

    public String getUrl() {
        return sourceInfo.getUrl();
    }

    private HttpURLConnection openConnection(long offset, int timeout) {
        try {
            URL url = new URL(sourceInfo.getUrl());
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            if (connection instanceof HttpsURLConnection) {
                HttpsURLConnection httpsConnection = (HttpsURLConnection) connection;
                SSLContext sslContext = SSLContext.getInstance("SSL");
                sslContext.init(null, trustAllCerts, new java.security.SecureRandom());
                SSLSocketFactory sslSocketFactory = sslContext.getSocketFactory();
                httpsConnection.setSSLSocketFactory(sslSocketFactory);
                httpsConnection.setHostnameVerifier(v);
            }
            connection.setRequestProperty("Range", "bytes=" + offset + "-");
            connection.setConnectTimeout(timeout);
            connection.setReadTimeout(timeout);
            injectCustomHeaders(connection, sourceInfo.getUrl());
            return connection;
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    private void injectCustomHeaders(HttpURLConnection connection, String url) {
        if (headerInjector != null) {
            headerInjector.inject(connection, url);
        }
    }

    private long readSourceAvailableBytes(HttpURLConnection connection, long offset, int timeout) {
        connection.disconnect();
        connection = openConnection(offset, timeout);
        long contentLength = getContentLength(connection);
        connection.disconnect();
        return contentLength;
    }

    private long getContentLength(HttpURLConnection connection) {
        String contentLengthHeader = connection.getHeaderField("Content-Length");
        if (contentLengthHeader != null) {
            return Long.parseLong(contentLengthHeader);
        }
        return -1;
    }

    private void fetchContentInfo() {
        if (sourceInfoStorage != null) {
            SourceInfo cachedSourceInfo = sourceInfoStorage.get(sourceInfo.getUrl());
            if (cachedSourceInfo != null) {
                sourceInfo = cachedSourceInfo;
                return;
            }
        }
        long contentLength = readSourceAvailableBytes(connection, 0, 5000);
        String mime = connection.getHeaderField("Content-Type");
        sourceInfo.setLength(contentLength);
        sourceInfo.setMime(mime);
        if (sourceInfoStorage != null) {
            sourceInfoStorage.put(sourceInfo);
        }
    }
