import java.io.*;
import java.net.HttpURLConnection;
import java.net.Proxy;
import java.net.URL;
import java.nio.charset.StandardCharsets;
import java.util.Map;
import java.util.zip.GZIPOutputStream;

public class HttpService {

    private static final boolean shouldGzipRequestPayload = true;
    private static final int MIN_UNAVAILABLE_HTTP_RESPONSE_CODE = 500;
    private static final int MAX_UNAVAILABLE_HTTP_RESPONSE_CODE = 599;
    private static final String LOGTAG = "HttpService";
    private static final int HTTP_OUTPUT_STREAM_BUFFER_SIZE = 8192;
    private static final String CONTENT_ENCODING_HEADER = "Content-Encoding";
    private static final String GZIP_CONTENT_TYPE_HEADER = "gzip";

    public byte[] performRequest(String endpointUrl, ProxyServerInteractor interactor, Map<String, Object> params, SSLSocketFactory socketFactory) throws IOException, ServiceUnavailableException {
        HttpURLConnection connection = null;
        OutputStream out = null;
        try {
            URL url = new URL(endpointUrl);
            connection = (HttpURLConnection) (interactor != null ? url.openConnection(interactor.getProxy()) : url.openConnection());
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.setConnectTimeout(5000);
            connection.setReadTimeout(5000);
            if (socketFactory != null) {
                connection.setSSLSocketFactory(socketFactory);
            }
            if (shouldGzipRequestPayload) {
                connection.setRequestProperty(CONTENT_ENCODING_HEADER, GZIP_CONTENT_TYPE_HEADER);
                out = getBufferedOutputStream(connection.getOutputStream());
                try (GZIPOutputStream gzipOut = new GZIPOutputStream(out)) {
                    gzipOut.write(paramsToString(params).getBytes(StandardCharsets.UTF_8));
                }
            } else {
                out = getBufferedOutputStream(connection.getOutputStream());
                out.write(paramsToString(params).getBytes(StandardCharsets.UTF_8));
            }
            int responseCode = connection.getResponseCode();
            if (responseCode >= MIN_UNAVAILABLE_HTTP_RESPONSE_CODE && responseCode <= MAX_UNAVAILABLE_HTTP_RESPONSE_CODE) {
                throw new ServiceUnavailableException("Service unavailable: " + responseCode);
            }
            return slurp(connection.getInputStream());
        } finally {
            if (out != null) {
                out.close();
            }
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private OutputStream getBufferedOutputStream(OutputStream out) {
        return new BufferedOutputStream(out, HTTP_OUTPUT_STREAM_BUFFER_SIZE);
    }

    private boolean isProxyRequest(String endpointUrl) {
        return endpointUrl.startsWith("http://proxy");
    }

    private byte[] slurp(InputStream inputStream) throws IOException {
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();
        byte[] buffer = new byte[HTTP_OUTPUT_STREAM_BUFFER_SIZE];
        int length;
        while ((length = inputStream.read(buffer)) != -1) {
            byteArrayOutputStream.write(buffer, 0, length);
        }
        return byteArrayOutputStream.toByteArray();
    }

    private String paramsToString(Map<String, Object> params) {
        StringBuilder stringBuilder = new StringBuilder();
        for (Map.Entry<String, Object> entry : params.entrySet()) {
            stringBuilder.append(entry.getKey()).append("=").append(entry.getValue()).append("&");
        }
        return stringBuilder.toString();
    }
}
