    public static String parseQueries(HashMap<String, Object> query) {
        StringBuilder queryBuilder = new StringBuilder();
        for (Map.Entry<String, Object> entry : query.entrySet()) {
            if (queryBuilder.length() > 0) {
                queryBuilder.append("&");
            }
            try {
                queryBuilder.append(URLEncoder.encode(entry.getKey(), "UTF-8"));
                queryBuilder.append("=");
                queryBuilder.append(URLEncoder.encode(entry.getValue().toString(), "UTF-8"));
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
        return queryBuilder.toString();
    }

