        String response = postRaw(headers, url, new Gson().toJson(body));
        if (response != null) {
            try {
                return new Gson().fromJson(response, tClass);
            } catch (JsonSyntaxException e) {
                e.printStackTrace();
            }
        }
        return null;
    }

    public static <T> T postFullUrl(Map<String, String> headers, String url, HashMap<String, Object> query, T body, Class<T> tClass) {
        return postFullUrl(headers, url + "?" + parseQueries(query), body, tClass);
    }

    public static String urlEncodeUTF8(String input) {
        try {
            return URLEncoder.encode(input, "UTF-8");
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }
}
