import java.io.InputStream;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;

public class HttpWeatherClient {

    public static class WeatherAsyncTask extends AsyncTask<String, String, String> {
        @Override
        protected String doInBackground(String... params) {
            try {
                String weatherData;
                URL url = new URL(params[0]);
                HttpURLConnection urlConnection = (HttpURLConnection) url.openConnection();
                urlConnection.setConnectTimeout(7000);

                try {
                    InputStream in = new BufferedInputStream(urlConnection.getInputStream());
                    weatherData = convertStreamToString(in);
                    publishProgress(weatherData);
                } finally {
                    urlConnection.disconnect();
                }
            } catch (Exception ex) {}
            return null;
        }

        protected void onProgressUpdate(String... progress) {
            try {
                JSONObject json = new JSONObject(progress[0]);
                JSONObject query = json.getJSONObject("query");
                JSONObject results = query.getJSONObject("results");
                JSONObject channel = results.getJSONObject("channel");
                JSONObject astronomy = channel.getJSONObject("astronomy");
                String sunset = astronomy.getString("sunset");
                String sunrise = astronomy.getString("sunrise");
            } catch (Exception ex) {}
        }
    }

    private static String convertStreamToString(InputStream inputStream) {
        BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
        String line;
        String result = "";

        try {
            while ((line = reader.readLine()) != null) {
                result += line;
            }
            inputStream.close();
        } catch (Exception ex) {}

        return result;
    }
}

