import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.function.Consumer;

public class OsmInputFile {
    private final Path path;
    private final boolean lazy;

    public OsmInputFile(Path path, boolean lazy) {
        this.path = path;
        this.lazy = lazy;
    }

    public int readInt(FileChannel channel) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        channel.read(buffer);
        buffer.flip();
        return buffer.getInt();
    }

    public byte[] readBytes(FileChannel channel, int length) throws IOException {
        byte[] bytes = new byte[length];
        ByteBuffer buffer = ByteBuffer.wrap(bytes);
        channel.read(buffer);
        return bytes;
    }

    public byte[] readBytes(FileChannel channel, long offset, int length) throws IOException {
        byte[] bytes = new byte[length];
        ByteBuffer buffer = ByteBuffer.wrap(bytes);
        channel.read(buffer, offset);
        return bytes;
    }

    public BlobHeader readBlobHeader(FileChannel channel) throws IOException {
        int headerSize = readInt(channel);
        byte[] headerBytes = readBytes(channel, headerSize);
        return BlobHeader.parseFrom(headerBytes);
    }

    public FileChannel openChannel() throws IOException {
        return FileChannel.open(path, StandardOpenOption.READ);
    }

    public LazyReader lazyReader() {
        return new LazyReader();
    }

    public class LazyReader implements AutoCloseable {
        private final FileChannel channel;

        public LazyReader() {
            try {
                this.channel = openChannel();
            } catch (IOException e) {
                throw new RuntimeException("Failed to open channel", e);
            }
        }

        public void forEachBlock(Consumer<Block> consumer) throws IOException {
            long offset = 0;
            while (offset < channel.size()) {
                channel.position(offset);
                BlobHeader header = readBlobHeader(channel);
                int length = header.getDatasize();
                consumer.accept(new LazyBlock(offset + 4 + header.getSerializedSize(), length, channel));
                offset += 4 + header.getSerializedSize() + length;
            }
        }

        @Override
        public void close() throws IOException {
            channel.close();
        }
    }

    public record LazyBlock(long offset, int length, FileChannel channel) implements Block {
        public Iterable<OsmElement> decodeElements() {
            try {
                byte[] data = readBytes(channel, offset, length);
                return Block.decode(data);
            } catch (IOException e) {
                throw new RuntimeException("Failed to decode block", e);
            }
        }
    }
}

