import javax.net.ssl.SSLContext;
import java.util.concurrent.TimeUnit;

public class ProxyClientContainer extends ChannelInitializer<SocketChannel> {
    private static final Logger logger = LoggerFactory.getLogger(ProxyClientContainer.class);
    private final EventLoopGroup workerGroup = new NioEventLoopGroup();
    private final Bootstrap bootstrap = new Bootstrap();
    private final Bootstrap realServerBootstrap = new Bootstrap();
    private final Config config;
    private final SSLContext sslContext;
    private long sleepTimeMill = 1000;

    public ProxyClientContainer(Config config, SSLContext sslContext) {
        this.config = config;
        this.sslContext = sslContext;
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .handler(this);
        realServerBootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true);
    }

    public void start() {
        connectProxyServer();
    }

    private ChannelHandler createSslHandler(SSLContext sslContext) {
        return new SslHandler(sslContext.createSSLEngine());
    }

    private void connectProxyServer() {
        bootstrap.connect(config.getProxyHost(), config.getProxyPort())
                .addListener(future -> {
                    if (!future.isSuccess()) {
                        logger.error("Failed to connect to proxy server", future.cause());
                        channelInactive(null);
                    } else {
                        sleepTimeMill = 1000;
                        logger.info("Connected to proxy server");
                    }
                });
    }

    public void stop() {
        workerGroup.shutdownGracefully();
    }

    @Override
    public void channelInactive(ChannelHandlerContext ctx) {
        logger.warn("Connection to proxy server lost, attempting to reconnect");
        reconnectWait();
    }

    private void reconnectWait() {
        workerGroup.schedule(() -> {
            sleepTimeMill = Math.min(sleepTimeMill * 2, 60000);
            connectProxyServer();
        }, sleepTimeMill, TimeUnit.MILLISECONDS);
    }

    @Override
    protected void initChannel(SocketChannel ch) {
        ChannelPipeline pipeline = ch.pipeline();
        if (sslContext != null) {
            pipeline.addLast(createSslHandler(sslContext));
        }
        pipeline.addLast(new IdleStateHandler(0, 30, 0));
    }
}

