import java.util.concurrent.TimeUnit;

public class NettyRemotingClient {
    private final Bootstrap bootstrap;
    private final EventLoopGroup eventLoopGroupWorker;

    public NettyRemotingClient() {
        this.eventLoopGroupWorker = new NioEventLoopGroup();
        this.bootstrap = new Bootstrap();
        this.bootstrap.group(this.eventLoopGroupWorker)
            .channel(NioSocketChannel.class)
            .option(ChannelOption.TCP_NODELAY, true)
            .handler(new ChannelInitializer<SocketChannel>() {
                @Override
                protected void initChannel(SocketChannel ch) throws Exception {
                    ChannelPipeline pipeline = ch.pipeline();
                    pipeline.addLast(new IdleStateHandler(0, 0, 30, TimeUnit.SECONDS));
                    pipeline.addLast(new NettyEncoder());
                    pipeline.addLast(new NettyDecoder());
                    pipeline.addLast(new NettyClientHandler());
                }
            });
    }

    public ChannelFuture createChannel(String addr) throws RemotingConnectException {
        String[] parts = addr.split(":");
        String host = parts[0];
        int port = Integer.parseInt(parts[1]);
        ChannelFuture channelFuture = this.bootstrap.connect(host, port);
        if (!channelFuture.awaitUninterruptibly(3000)) {
            throw new RemotingConnectException("Connect to " + addr + " timeout");
        }
        if (!channelFuture.isSuccess()) {
            throw new RemotingConnectException("Failed to connect to " + addr, channelFuture.cause());
        }
        return channelFuture;
    }

    public CompletableFuture<RemotingCommand> invokeSync(Channel channel, RemotingCommand request, long timeoutMillis) {
        CompletableFuture<RemotingCommand> future = new CompletableFuture<>();
        try {
            channel.writeAndFlush(request).addListener(f -> {
                if (!f.isSuccess()) {
                    future.completeExceptionally(f.cause());
                }
            });
        } catch (Exception e) {
            future.completeExceptionally(e);
        }
        return future;
    }

    public void shutdown() {
        if (this.eventLoopGroupWorker != null) {
            this.eventLoopGroupWorker.shutdownGracefully();
        }
    }

    private class NettyClientHandler extends SimpleChannelInboundHandler<RemotingCommand> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, RemotingCommand msg) throws Exception {
            ctx.fireChannelRead(msg);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) throws Exception {
            ctx.close();
        }
    }
}

