import java.io.DataOutputStream;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.logging.Logger;
import java.lang.management.ManagementFactory;
import sun.misc.Unsafe;
import java.lang.reflect.Field;

public abstract class AppendStoreMmap {
    protected DataOutputStream outputStream;
    protected final int segmentBits;
    protected final long segmentMask;
    protected final long segmentBytes;
    protected final Path path;
    protected final boolean madvise;
    protected long outIdx;
    protected MappedByteBuffer[] segments;
    protected FileChannel channel;
    protected static final Logger LOGGER = Logger.getLogger(AppendStoreMmap.class.getName());

    public AppendStoreMmap(Path path, boolean madvise) throws IOException {
        this(path, 1L << 30, madvise);
    }

    public AppendStoreMmap(Path path, long segmentSizeBytes, boolean madvise) throws IOException {
        this.path = path;
        this.madvise = madvise;
        this.segmentBytes = segmentSizeBytes;
        this.segmentBits = Long.numberOfTrailingZeros(segmentSizeBytes);
        this.segmentMask = segmentSizeBytes - 1;
        this.outIdx = 0;
        this.segments = null;

        this.channel = FileChannel.open(path, StandardOpenOption.READ, StandardOpenOption.WRITE, StandardOpenOption.CREATE);
        this.outputStream = new DataOutputStream(new java.io.BufferedOutputStream(java.nio.file.Files.newOutputStream(path, StandardOpenOption.WRITE, StandardOpenOption.APPEND)));
    }

    protected synchronized MappedByteBuffer[] getSegments() throws IOException {
        if (segments == null) {
            long fileSize = channel.size();
            int numSegments = (int)((fileSize + segmentBytes - 1) / segmentBytes);
            segments = new MappedByteBuffer[numSegments];
            for (int i = 0; i < numSegments; i++) {
                long segmentStart = i * segmentBytes;
                long segmentEnd = Math.min(fileSize, segmentStart + segmentBytes);
                long segmentLength = segmentEnd - segmentStart;
                segments[i] = channel.map(FileChannel.MapMode.READ_ONLY, segmentStart, segmentLength);
                if (madvise) {
                    try {
                        Field f = Unsafe.class.getDeclaredField("theUnsafe");
                        f.setAccessible(true);
                        Unsafe unsafe = (Unsafe) f.get(null);
                        unsafe.invokeCleaner(segments[i]);
                    } catch (Exception e) {
                        LOGGER.warning("Failed to madvise: " + e.getMessage());
                    }
                }
            }
        }
        return segments;
    }

    public synchronized void close() throws IOException {
        if (outputStream != null) {
            outputStream.close();
            outputStream = null;
        }
        if (segments != null) {
            for (MappedByteBuffer segment : segments) {
                if (segment != null) {
                    try {
                        Field f = Unsafe.class.getDeclaredField("theUnsafe");
                        f.setAccessible(true);
                        Unsafe unsafe = (Unsafe) f.get(null);
                        unsafe.invokeCleaner(segment);
                    } catch (Exception e) {
                        LOGGER.warning("Failed to unmap: " + e.getMessage());
                    }
                }
            }
            segments = null;
        }
        if (channel != null) {
            channel.close();
            channel = null;
        }
    }

    public long diskUsageBytes() throws IOException {
        return channel.size();
    }
}

