import java.net.URI;
import java.time.Duration;

public class NettyTcpClient {

    private static final Logger LOG = LoggerFactory.getLogger(NettyTcpClient.class);
    private static final Duration DEFAULT_CONNECTION_TIMEOUT = Duration.ofSeconds(10);
    private static final int DEFAULT_IDLE_TIMEOUT = 15;

    private URI uri;
    private Channel webSocketChannel;
    private NioEventLoopGroup eventLoopGroup;
    private boolean compressedMessages = false;
    private boolean acceptAllCertificates = false;
    private boolean enableLoggingHandler = false;
    private LogLevel loggingHandlerLevel = LogLevel.DEBUG;
    private String socksProxyHost;
    private Integer socksProxyPort;

    public void connect() {
        try {
            String scheme = uri.getScheme() == null ? "ws" : uri.getScheme();
            String host = uri.getHost();
            if (host == null) {
                throw new IllegalArgumentException("Host cannot be null.");
            }

            final int port = uri.getPort() == -1 ? 
                ("ws".equalsIgnoreCase(scheme) ? 80 : 443) : uri.getPort();

            final boolean ssl = "wss".equalsIgnoreCase(scheme);
            final SslContext sslCtx = ssl ? 
                SslContextBuilder.forClient()
                    .trustManager(acceptAllCertificates ? InsecureTrustManagerFactory.INSTANCE : null)
                    .build() : null;

            WebSocketClientHandler handler = getWebSocketClientHandler(
                WebSocketClientHandshakerFactory.newHandshaker(
                    uri, WebSocketVersion.V13, null, true, new DefaultHttpHeaders(), 65536),
                message -> {});

            if (eventLoopGroup == null || eventLoopGroup.isShutdown()) {
                eventLoopGroup = new NioEventLoopGroup(2);
            }

            new Bootstrap()
                .group(eventLoopGroup)
                .option(ChannelOption.CONNECT_TIMEOUT_MILLIS, 
                    (int) DEFAULT_CONNECTION_TIMEOUT.toMillis())
                .option(ChannelOption.SO_KEEPALIVE, true)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline p = ch.pipeline();
                        if (socksProxyHost != null) {
                            p.addLast(new Socks5ProxyHandler(
                                SocketUtils.socketAddress(socksProxyHost, socksProxyPort)));
                        }
                        if (sslCtx != null) {
                            p.addLast(sslCtx.newHandler(ch.alloc(), host, port));
                        }
                        p.addLast(new HttpClientCodec());
                        if (enableLoggingHandler) {
                            p.addLast(new LoggingHandler(loggingHandlerLevel));
                        }
                        if (compressedMessages) {
                            p.addLast(WebSocketClientCompressionHandler.INSTANCE);
                        }
                        p.addLast(new HttpObjectAggregator(8192));
                        if (DEFAULT_IDLE_TIMEOUT > 0) {
                            p.addLast(new IdleStateHandler(DEFAULT_IDLE_TIMEOUT, 0, 0));
                        }
                        WebSocketClientExtensionHandler clientExtensionHandler = 
                            getWebSocketClientExtensionHandler();
                        if (clientExtensionHandler != null) {
                            p.addLast(clientExtensionHandler);
                        }
                        p.addLast(handler);
                    }
                })
                .connect(uri.getHost(), port)
                .addListener((ChannelFuture channelFuture) -> {
                    webSocketChannel = channelFuture.channel();
                    if (!channelFuture.isSuccess()) {
                        LOG.error("Connection failed", channelFuture.cause());
                    }
                });
        } catch (Exception e) {
            LOG.error("Connection error", e);
        }
    }

    public void disconnect() {
        if (webSocketChannel != null) {
            webSocketChannel.close();
        }
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully(2, DEFAULT_IDLE_TIMEOUT, TimeUnit.SECONDS);
        }
    }

    protected WebSocketClientExtensionHandler getWebSocketClientExtensionHandler() {
        return WebSocketClientCompressionHandler.INSTANCE;
    }

    protected WebSocketClientHandler getWebSocketClientHandler(
        WebSocketClientHandshaker handshaker,
        WebSocketClientHandler.WebSocketMessageHandler handler) {
        return new WebSocketClientHandler(handshaker, handler) {
            @Override
            public void channelInactive(ChannelHandlerContext ctx) {
                LOG.info("Channel inactive");
                ctx.close();
            }

            @Override
            public void userEventTriggered(ChannelHandlerContext ctx, Object evt) {
                if (evt instanceof IdleStateEvent) {
