                        if (socksProxyHost != null && socksProxyPort != null) {
                            pipeline.addFirst(new Socks5ProxyHandler(
                                    new java.net.InetSocketAddress(socksProxyHost, socksProxyPort)));
                        }
                        if (ssl) {
                            pipeline.addLast(sslContext.newHandler(ch.alloc(), host, port));
                        }
                        if (enableLoggingHandler) {
                            pipeline.addLast(new LoggingHandler(loggingHandlerLevel));
                        }
                        pipeline.addLast(new HttpClientCodec());
                        pipeline.addLast(new HttpObjectAggregator(8192));
                        if (compressedMessages) {
                            pipeline.addLast(WebSocketClientCompressionHandler.INSTANCE);
                        }
                        pipeline.addLast(new IdleStateHandler(0, 30, 0, TimeUnit.SECONDS));
                        pipeline.addLast(new WebSocketClientHandler(handshaker, null));
                    }
                });

        ChannelFuture future = bootstrap.connect(host, port).sync();
        webSocketChannel = future.channel();
    }

    public void disconnect() {
