    private EventLoopGroup eventLoopGroup;
    private int connectTimeout;
    private int readTimeout;
    private String threadPrefix;

    public void init(DockerClientConfig dockerClientConfig) {
        this.connectTimeout = dockerClientConfig.getConnectTimeout();
        this.readTimeout = dockerClientConfig.getReadTimeout();
        this.threadPrefix = dockerClientConfig.getThreadPrefix();
        this.eventLoopGroup = new NioEventLoopGroup(1, new DefaultThreadFactory(threadPrefix));
        this.bootstrap = new Bootstrap()
                .group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.CONNECT_TIMEOUT_MILLIS, connectTimeout)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ch.pipeline().addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
                    }
                });
    }

    public DuplexChannel connect(DockerClientConfig dockerClientConfig) throws Exception {
        if (dockerClientConfig.isSsl()) {
            SslHandler sslHandler = initSsl(dockerClientConfig);
            bootstrap.handler(new ChannelInitializer<SocketChannel>() {
                @Override
                protected void initChannel(SocketChannel ch) {
                    ch.pipeline().addFirst(sslHandler);
                    ch.pipeline().addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
                }
            });
        }
        Channel channel = bootstrap.connect(dockerClientConfig.getHost(), dockerClientConfig.getPort()).sync().channel();
        return (DuplexChannel) channel;
    }

    private SslHandler initSsl(DockerClientConfig dockerClientConfig) throws Exception {
        SSLContext sslContext = SSLContext.getInstance("TLS");
        sslContext.init(dockerClientConfig.getKeyManagers(), dockerClientConfig.getTrustManagers(), null);
        SSLSocketFactory sslSocketFactory = sslContext.getSocketFactory();
        SSLParameters sslParameters = sslSocketFactory.getDefaultSSLParameters();
        enableHostNameVerification(sslParameters);
        SslHandler sslHandler = new SslHandler(sslSocketFactory.createSocket());
        sslHandler.setHandshakeTimeoutMillis(connectTimeout);
        return sslHandler;
    }

    private SSLParameters enableHostNameVerification(SSLParameters sslParameters) {
        sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
        return sslParameters;
    }

    public void close() {
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
    }

    private <T extends Channel> T configure(T channel) {
        channel.config().setConnectTimeoutMillis(connectTimeout);
        channel.pipeline().addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
        return channel;
    }

    private class ReadTimeoutHandler extends io.netty.handler.timeout.ReadTimeoutHandler {
        public ReadTimeoutHandler(int timeout, TimeUnit unit) {
            super(timeout, unit);
        }

        @Override
        protected void readTimedOut(io.netty.channel.ChannelHandlerContext ctx) {
            ctx.fireExceptionCaught(new ConnectTimeoutException("Read timeout"));
        }
    }
}

