import io.netty.handler.codec.http.HttpObjectAggregator;
import io.netty.handler.codec.http.HttpServerCodec;
import io.netty.handler.codec.http.websocketx.WebSocketServerProtocolHandler;
import io.netty.handler.logging.LogLevel;
import io.netty.handler.logging.LoggingHandler;
import java.util.ArrayList;
import java.util.List;
import java.util.logging.Logger;

public class LocalServer {
    private int port;
    private List<Protocol> protocolList;
    private Logger logger;

    public LocalServer(int port) {
        this.port = port;
        this.protocolList = new ArrayList<>();
        this.logger = Logger.getLogger(LocalServer.class.getName());
    }

    public void listen(int port) {
        this.port = port;
    }

    public void start() {
        EventLoopGroup bossGroup = new NioEventLoopGroup();
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .handler(new LoggingHandler(LogLevel.INFO))
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) {
                     ch.pipeline().addLast(
                         new HttpServerCodec(),
                         new HttpObjectAggregator(65536),
                         new WebSocketServerProtocolHandler("/"),
                         new LocalServerWebsocketEventImp());
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 128)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            logger.severe("Server interrupted: " + e.getMessage());
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private class LocalServerWebsocketEventImp extends WebSocketServerProtocolHandler {
        public void onConnect(ChannelHandlerContext ctx) {
            logger.info("Client connected: " + ctx.channel().remoteAddress());
        }

        public void onDisconnect(ChannelHandlerContext ctx) {
            logger.info("Client disconnected: " + ctx.channel().remoteAddress());
            protocolList.removeIf(protocol -> protocol.getChannel() == ctx.channel());
        }

        public void onTextMessage(ChannelHandlerContext ctx, String text) {
            try {
                Command command = Command.parse(text);
                if (command.getType() == CommandType.INIT) {
                    initLocalClient(ctx, command);
                } else {
                    executeCommand(ctx, command);
                }
            } catch (Exception e) {
                logger.warning("Error processing text message: " + e.getMessage());
            }
        }

        public void onBinaryMessage(ChannelHandlerContext ctx, byte[] data) {
            Protocol protocol = protocolList.stream()
                .filter(p -> p.getChannel() == ctx.channel())
                .findFirst()
                .orElse(null);
            if (protocol != null) {
                protocol.processBinaryData(data);
            }
        }

        private void initLocalClient(ChannelHandlerContext ctx, Command command) {
            Protocol protocol = new Protocol(ctx.channel());
            protocolList.add(protocol);
            protocol.initialize(command);
        }

        private void executeCommand(ChannelHandlerContext ctx, Command command) {
            Protocol protocol = protocolList.stream()
                .filter(p -> p.getChannel() == ctx.channel())
                .findFirst()
                .orElse(null);
            if (protocol != null) {
                protocol.executeCommand(command);
            }
        }
    }
}

