import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.util.concurrent.DefaultThreadFactory;
import java.util.concurrent.ThreadPoolExecutor;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.concurrent.LinkedBlockingQueue;
import java.util.concurrent.TimeUnit;

public class NettyServer {
    private NettyServerChannelManage channelManage;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Channel serverChannel;
    private MessageHandler messageHandler;
    private ThreadPoolExecutor threadPoolExecutor;
    private AtomicInteger rejectCounter;
    private Logger logger;
    private URL url;
    private ChannelState state;
    private Codec codec;

    public NettyServer(URL url, MessageHandler messageHandler) {
        this.url = url;
        this.messageHandler = messageHandler;
        this.channelManage = new NettyServerChannelManage();
        this.rejectCounter = new AtomicInteger(0);
        this.state = ChannelState.UNINIT;
        this.codec = new Codec();
        this.logger = LoggerFactory.getLogger(NettyServer.class);
    }

    public boolean open() {
        if (state == ChannelState.OPEN) {
            return true;
        }
        state = ChannelState.OPENING;
        int threads = url.getParameter("threads", Runtime.getRuntime().availableProcessors() * 2);
        threadPoolExecutor = new ThreadPoolExecutor(threads, threads, 0, TimeUnit.MILLISECONDS,
                new LinkedBlockingQueue<>(url.getParameter("queues", 1000)),
                new DefaultThreadFactory("NettyServerWorker", true),
                (r, executor) -> {
                    rejectCounter.incrementAndGet();
                    throw new RejectedExecutionException("Task " + r.toString() + " rejected from " + executor.toString());
                });

        bossGroup = new NioEventLoopGroup(1, new DefaultThreadFactory("NettyServerBoss", true));
        workerGroup = new NioEventLoopGroup(url.getParameter("iothreads", Runtime.getRuntime().availableProcessors() * 2),
                new DefaultThreadFactory("NettyServerWorker", true));

        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(bossGroup, workerGroup)
                .channel(NioServerSocketChannel.class)
                .childOption(ChannelOption.TCP_NODELAY, Boolean.TRUE)
                .childOption(ChannelOption.SO_REUSEADDR, Boolean.TRUE)
                .childOption(ChannelOption.ALLOCATOR, PooledByteBufAllocator.DEFAULT)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ch.pipeline().addLast("decoder", codec.getDecoder())
                                .addLast("encoder", codec.getEncoder())
                                .addLast("handler", new NettyServerHandler(messageHandler, threadPoolExecutor));
                    }
                });

        try {
            ChannelFuture future = bootstrap.bind(url.getPort()).sync();
            serverChannel = future.channel();
            state = ChannelState.OPEN;
            return true;
        } catch (InterruptedException e) {
            state = ChannelState.CLOSE;
            Thread.currentThread().interrupt();
            throw new IllegalStateException("Failed to bind " + url.getPort(), e);
        } catch (Exception e) {
            state = ChannelState.CLOSE;
            throw new IllegalStateException("Failed to bind " + url.getPort(), e);
        }
    }

    public void close(int timeout) {
        if (state != ChannelState.OPEN) {
            return;
        }
        state = ChannelState.CLOSING;
        try {
            if (serverChannel != null) {
                serverChannel.close().syncUninterruptibly();
            }
            cleanup();
        } finally {
            state = ChannelState.CLOSE;
        }
    }

    private void cleanup() {
        if (bossGroup != null) {
            bossGroup.shutdownGracefully();
        }
        if (workerGroup != null) {
            workerGroup.shutdownGracefully();
        }
        if (threadPoolExecutor != null) {
            threadPoolExecutor.shutdown();
            try {
                threadPoolExecutor.awaitTermination(timeout, TimeUnit.MILLISECONDS);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
        channelManage.closeAll();
    }
}

