        this.xxlRpcProviderFactory = xxlRpcProviderFactory;
        thread = new Thread(() -> {
            bossGroup = new NioEventLoopGroup(1, new ThreadFactory() {
                private AtomicInteger threadIndex = new AtomicInteger(0);
                @Override
                public Thread newThread(Runnable r) {
                    return new Thread(r, "NettyServerBossThread-" + threadIndex.incrementAndGet());
                }
            });
            workerGroup = new NioEventLoopGroup(xxlRpcProviderFactory.getCorePoolSize(), new ThreadFactory() {
                private AtomicInteger threadIndex = new AtomicInteger(0);
                @Override
                public Thread newThread(Runnable r) {
                    return new Thread(r, "NettyServerWorkerThread-" + threadIndex.incrementAndGet());
                }
            });

            try {
                ServerBootstrap bootstrap = new ServerBootstrap();
                bootstrap.group(bossGroup, workerGroup)
                        .channel(NioServerSocketChannel.class)
                        .childHandler(new ChannelInitializer<SocketChannel>() {
                            @Override
                            public void initChannel(SocketChannel channel) throws Exception {
                                channel.pipeline()
                                        .addLast(new IdleStateHandler(0, 0, xxlRpcProviderFactory.getBeatInterval() * 3))
                                        .addLast(new NettyDecoder(xxlRpcProviderFactory.getSerializer()))
                                        .addLast(new NettyEncoder(xxlRpcProviderFactory.getSerializer()))
                                        .addLast(new NettyServerHandler(xxlRpcProviderFactory));
                            }
                        })
                        .childOption(ChannelOption.TCP_NODELAY, true)
                        .childOption(ChannelOption.SO_KEEPALIVE, true);

                ChannelFuture future = bootstrap.bind(xxlRpcProviderFactory.getPort()).sync();
                future.channel().closeFuture().sync();
            } catch (InterruptedException e) {
                if (xxlRpcProviderFactory != null) {
                    xxlRpcProviderFactory.getLog().error("Netty server interrupted", e);
                }
            } finally {
                stop();
            }
        });
        thread.setDaemon(true);
        thread.start();
    }

    public void stop() {
        if (bossGroup != null) {
            bossGroup.shutdownGracefully();
        }
        if (workerGroup != null) {
            workerGroup.shutdownGracefully();
        }
        if (thread != null && thread.isAlive()) {
            thread.interrupt();
        }
        if (xxlRpcProviderFactory != null) {
            xxlRpcProviderFactory.getLog().info("Netty server stopped");
        }
    }
}

