import io.netty.channel.socket.nio.NioServerSocketChannel;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public abstract class AbstractHttp2ServerTransport {
    private static final Logger LOGGER = LoggerFactory.getLogger(AbstractHttp2ServerTransport.class);
    protected final ServerBootstrap serverBootstrap;
    protected final EventLoopGroup bizGroup;
    protected final ServerTransportConfig transportConfig;
    protected Channel serverChannel;

    public AbstractHttp2ServerTransport(ServerTransportConfig transportConfig) {
        this.transportConfig = transportConfig;
        this.bizGroup = new NioEventLoopGroup(transportConfig.getWorkerThreads());
        this.serverBootstrap = new ServerBootstrap();
    }

    public boolean start() {
        try {
            serverBootstrap.group(bizGroup)
                .channel(NioServerSocketChannel.class)
                .option(ChannelOption.SO_BACKLOG, transportConfig.getBacklog())
                .option(ChannelOption.SO_REUSEADDR, transportConfig.isReuseAddress())
                .childOption(ChannelOption.TCP_NODELAY, transportConfig.isTcpNoDelay())
                .childOption(ChannelOption.SO_KEEPALIVE, transportConfig.isKeepAlive())
                .childHandler(createChannelInitializer());

            serverChannel = serverBootstrap.bind(transportConfig.getHost(), transportConfig.getPort()).sync().channel();
            LOGGER.info("HTTP/2 server started on {}:{}", transportConfig.getHost(), transportConfig.getPort());
            return true;
        } catch (InterruptedException e) {
            LOGGER.error("Server startup interrupted", e);
            Thread.currentThread().interrupt();
            return false;
        } catch (Exception e) {
            LOGGER.error("Failed to start HTTP/2 server", e);
            return false;
        }
    }

    public void stop() {
        if (serverChannel != null) {
            try {
                serverChannel.close().sync();
            } catch (InterruptedException e) {
                LOGGER.error("Server shutdown interrupted", e);
                Thread.currentThread().interrupt();
            }
        }
        if (bizGroup != null) {
            bizGroup.shutdownGracefully();
        }
        LOGGER.info("HTTP/2 server stopped");
    }

    protected abstract Http2ChannelInitializer createChannelInitializer();
}

