                connections.add(connection);
                if (onConnect != null) {
                    onConnect.accept(connection);
                }
                ch.closeFuture().addListener(f -> connections.remove(connection)).addListener(f -> connection.close());
            }
        };
    }

    public void close() {
        eventLoopGroup.shutdownGracefully();
    }

}

