import io.netty.handler.logging.LoggingHandler;
import io.reactivex.subjects.Subject;

public class NettySourceHttpServer {
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Runnable nettyServerRunnable;
    private boolean isInitialized = false;
    private boolean isStarted = false;
    private Channel serverChannel;

    public NettySourceHttpServer(int threadCount) {
        this.bossGroup = new NioEventLoopGroup(threadCount);
        this.workerGroup = new NioEventLoopGroup();
    }

    public void init(QueryRegistry queryRegistry, Subject<String, String> eventSubject, int port) {
        if (isInitialized) {
            return;
        }

        nettyServerRunnable = () -> {
            try {
                ServerBootstrap b = new ServerBootstrap();
                b.group(bossGroup, workerGroup)
                 .channel(NioServerSocketChannel.class)
                 .handler(new LoggingHandler(LogLevel.INFO))
                 .childHandler(new ChannelInitializer<SocketChannel>() {
                     @Override
                     public void initChannel(SocketChannel ch) {
                         ch.pipeline().addLast(new HttpServerCodec());
                         ch.pipeline().addLast(new HttpObjectAggregator(1048576));
                         ch.pipeline().addLast(new HttpRequestHandler(queryRegistry, eventSubject));
                     }
                 })
                 .option(ChannelOption.SO_BACKLOG, 128)
                 .childOption(ChannelOption.SO_KEEPALIVE, true);

                serverChannel = b.bind(port).sync().channel();
                serverChannel.closeFuture().sync();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        };

        isInitialized = true;
    }

    public void startServer() {
        if (!isInitialized || isStarted) {
            return;
        }

        new Thread(nettyServerRunnable).start();
        isStarted = true;

        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            if (isStarted) {
                shutdownServer();
            }
        }));
    }

    public void shutdownServer() {
        if (!isInitialized || !isStarted) {
            return;
        }

        if (serverChannel != null) {
            serverChannel.close();
        }
        workerGroup.shutdownGracefully();
        bossGroup.shutdownGracefully();
        isStarted = false;
    }
}

