import java.io.File;
import java.io.IOException;
import java.nio.channels.FileChannel;
import java.nio.file.StandardOpenOption;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.CompletableFuture;

public abstract class TransferType {
    protected String path;
    protected File file;
    protected long fileSize;
    protected int bufferSize;
    protected long syncSize;
    protected long syncPosition;

    public TransferType(String path, DataStream cli) throws IOException {
        this.path = path;
        this.file = new File(path);
        this.fileSize = file.length();
        this.bufferSize = cli.getBufferSize();
        this.syncSize = cli.getSyncSize();
        this.syncPosition = 0;
    }

    public File getFile() {
        return file;
    }

    public int getBufferSize() {
        return bufferSize;
    }

    public long getPacketSize(long offset) {
        return Math.min(bufferSize, fileSize - offset);
    }

    public boolean isSync(long position) {
        return position >= syncPosition;
    }

    public List<CompletableFuture<DataStreamReply>> transfer(FileStoreClient client, RoutingTable routingTable) throws IOException {
        List<CompletableFuture<DataStreamReply>> futures = new ArrayList<>();
        try (FileChannel in = FileChannel.open(file.toPath(), StandardOpenOption.READ)) {
            DataStreamOutput out = client.getDataStreamOutput(routingTable);
            long offset = 0;
            while (offset < fileSize) {
                offset = write(in, out, offset, futures);
                if (isSync(offset)) {
                    syncPosition += syncSize;
                    futures.add(out.flushAsync());
                }
            }
            futures.add(out.closeAsync());
        }
        return futures;
    }

    public abstract long write(FileChannel in, DataStreamOutput out, long offset, List<CompletableFuture<DataStreamReply>> futures) throws IOException;

    @Override
    public String toString() {
        return "TransferType{" +
                "path='" + path + '\'' +
                ", fileSize=" + fileSize +
                ", bufferSize=" + bufferSize +
                ", syncSize=" + syncSize +
                ", syncPosition=" + syncPosition +
                '}';
    }
}

