import io.netty.handler.logging.LoggingHandler;

public class NettyHttpServer {
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Channel serverChannel;

    public int startServer() throws InterruptedException {
        bossGroup = new NioEventLoopGroup(1);
        workerGroup = new NioEventLoopGroup();
        ServerBootstrap b = new ServerBootstrap();
        b.group(bossGroup, workerGroup)
         .channel(NioServerSocketChannel.class)
         .handler(new LoggingHandler(LogLevel.INFO))
         .childHandler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) {
                 ChannelPipeline p = ch.pipeline();
                 p.addLast(new HttpServerCodec());
                 p.addLast(new HttpObjectAggregator(1048576));
                 p.addLast(new TracingDispatchHandler());
             }
         });

        ChannelFuture f = b.bind(0).sync();
        serverChannel = f.channel();
        return ((java.net.InetSocketAddress) serverChannel.localAddress()).getPort();
    }

    public void stopServer() {
        if (serverChannel != null) {
            serverChannel.close();
        }
        if (bossGroup != null) {
            bossGroup.shutdownGracefully();
        }
        if (workerGroup != null) {
            workerGroup.shutdownGracefully();
        }
    }

    private static class TracingDispatchHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) throws Exception {
            if (msg instanceof io.netty.handler.codec.http.HttpRequest) {
                io.netty.handler.codec.http.HttpRequest req = (io.netty.handler.codec.http.HttpRequest) msg;
                String path = req.uri();
                if ("/traced".equals(path)) {
                    ctx.pipeline().addAfter("aggregator", "tracingHandler", new TracingHandler(true));
                } else if ("/unsampled".equals(path)) {
                    ctx.pipeline().addAfter("aggregator", "tracingHandler", new TracingHandler(false));
                }
            }
            super.channelRead(ctx, msg);
        }

        @Override
        public void write(ChannelHandlerContext ctx, Object msg, ChannelPromise promise) throws Exception {
            super.write(ctx, msg, promise);
        }
    }

    private static class TracingHandler extends ChannelOutboundHandlerAdapter {
        private final boolean sampled;

        TracingHandler(boolean sampled) {
            this.sampled = sampled;
        }

        @Override
        public void write(ChannelHandlerContext ctx, Object msg, ChannelPromise promise) throws Exception {
            if (sampled) {
                ctx.write("Traced response\n", promise);
            } else {
                ctx.write("Unsampled response\n", promise);
            }
        }
    }
}

