import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.channels.SeekableByteChannel;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.util.HashMap;
import java.util.Map;

public class FLVWriter {
    private SeekableByteChannel fileChannel;
    private SeekableByteChannel dataChannel;
    private final String filePath;
    private final boolean append;
    private long appendOffset;
    private Map<String, ?> meta;

    public FLVWriter(String filePath) throws IOException {
        this.filePath = filePath;
        this.append = false;
        this.appendOffset = 0;
        this.meta = new HashMap<>();
        createDataFile();
    }

    public FLVWriter(Path path, boolean append) throws IOException {
        this.filePath = path.toString();
        this.append = append;
        if (append && Files.exists(path)) {
            this.meta = getMetaData(path, 10);
            this.appendOffset = Files.size(path);
        } else {
            this.meta = new HashMap<>();
            this.appendOffset = 0;
        }
        createDataFile();
    }

    private Map<String, ?> getMetaData(Path path, int maxTags) throws IOException {
        Map<String, Object> metadata = new HashMap<>();
        try (FileChannel channel = FileChannel.open(path, StandardOpenOption.READ)) {
            ByteBuffer header = ByteBuffer.allocate(9);
            channel.read(header);
            header.flip();
            if (header.get(0) != 'F' || header.get(1) != 'L' || header.get(2) != 'V') {
                throw new IOException("Not a valid FLV file");
            }
            header.position(4);
            long dataOffset = header.getInt() & 0xFFFFFFFFL;
            channel.position(dataOffset);
            int tagsRead = 0;
            while (tagsRead < maxTags) {
                ByteBuffer tagHeader = ByteBuffer.allocate(11);
                channel.read(tagHeader);
                tagHeader.flip();
                int tagType = tagHeader.get() & 0xFF;
                int dataSize = ((tagHeader.get() & 0xFF) << 16) | ((tagHeader.get() & 0xFF) << 8) | (tagHeader.get() & 0xFF);
                channel.position(channel.position() + dataSize + 4);
                if (tagType == 18) {
                    ByteBuffer metaBuffer = ByteBuffer.allocate(dataSize);
                    channel.read(metaBuffer);
                    metaBuffer.flip();
                    break;
                }
                tagsRead++;
            }
        }
        return metadata;
    }

    private void createDataFile() throws IOException {
        Path tempFile = Files.createTempFile("flvdata", ".tmp");
        tempFile.toFile().deleteOnExit();
        dataChannel = Files.newByteChannel(tempFile, StandardOpenOption.WRITE, StandardOpenOption.READ);
    }

    private void createOutputFile() throws IOException {
        Path path = Path.of(filePath);
        if (append && Files.exists(path)) {
            fileChannel = Files.newByteChannel(path, StandardOpenOption.WRITE, StandardOpenOption.READ);
        } else {
            fileChannel = Files.newByteChannel(path, StandardOpenOption.CREATE, StandardOpenOption.WRITE, StandardOpenOption.READ);
            writeHeader();
        }
    }

    private void writeHeader() throws IOException {
        ByteBuffer header = ByteBuffer.allocate(13);
        header.put((byte) 'F');
        header.put((byte) 'L');
        header.put((byte) 'V');
        header.put((byte) 1);
        header.put((byte) 5);
        header.putInt(9);
        header.putInt(0);
        header.flip();
        fileChannel.write(header);
    }

    public boolean writeTag(ITag tag) throws IOException {
        ByteBuffer tagBuffer = tag.toByteBuffer();
        return dataChannel.write(tagBuffer) == tagBuffer.capacity();
    }

    public void close() throws IOException {
        try {
            finalizeFlv();
        } finally {
            if (dataChannel != null) dataChannel.close();
            if (fileChannel != null) fileChannel.close();
        }
    }

    private long finalizeFlv() throws IOException {
        createOutputFile();
        if (append) {
            fileChannel.position(appendOffset);
        }
        dataChannel.position(0);
        ByteBuffer buffer = ByteBuffer.allocate(8192);
        while (dataChannel.read(buffer) != -1) {
            buffer.flip();
            fileChannel.write(buffer);
            buffer.clear();
        }
        return fileChannel.position();
    }
}

