public final class DatagramChannelEchoClient {

    private final String host;
    private final int port;
    private EventLoopGroup group;
    private Channel channel;

    public DatagramChannelEchoClient(String host, int port) {
        this.host = host;
        this.port = port;
    }

    public void start() throws InterruptedException {
        group = new NioEventLoopGroup();
        Bootstrap b = new Bootstrap();
        b.group(group)
         .channel(NioDatagramChannel.class)
         .option(ChannelOption.SO_BROADCAST, true)
         .handler(new DatagramChannelEchoClientHandler());

        ChannelFuture f = b.bind(port).sync();
        channel = f.channel();
        System.out.println("DatagramChannelEchoClient已启动，端口：" + port);
    }

    public void sendMessage(String message) throws IOException {
        ByteBuffer writeBuffer = ByteBuffer.allocate(32);
        writeBuffer.put(message.getBytes());
        writeBuffer.flip();
        writeBuffer.rewind();

        io.netty.buffer.ByteBuf buf = io.netty.buffer.Unpooled.copiedBuffer(writeBuffer);
        channel.writeAndFlush(new io.netty.channel.socket.DatagramPacket(
            buf, new InetSocketAddress(host, port)));
        writeBuffer.clear();
    }

    public void stop() {
        if (group != null) {
            group.shutdownGracefully();
        }
    }

