import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;

public class FileChannelProcessor {
    private FileChannel fileChannel;
    private final Path filePath;
    public static final int MAX_LINE_LENGTH = 1024;
    public static final int MMAP_CHUNK_SIZE = 8 * 1024 * 1024;

    public FileChannelProcessor(Path filePath) {
        this.filePath = filePath;
    }

    public void openChannel() throws IOException {
        if (fileChannel == null) {
            fileChannel = FileChannel.open(filePath, StandardOpenOption.READ);
        }
    }

    public MappedByteBuffer mapFileSegment(long start, long size) throws IOException {
        if (fileChannel == null) {
            throw new IllegalStateException("FileChannel is not open");
        }
        return fileChannel.map(FileChannel.MapMode.READ_ONLY, start, size);
    }

    public ByteBuffer readBufferSegment(long position, int length) throws IOException {
        if (fileChannel == null) {
            throw new IllegalStateException("FileChannel is not open");
        }
        ByteBuffer buffer = ByteBuffer.allocateDirect(length);
        fileChannel.read(buffer, position);
        buffer.flip();
        return buffer;
    }

    public long findLineBoundary(ByteBuffer buffer, long startPosition) {
        buffer.mark();
        int newLinePos = -1;
        for (int i = 0; i < Math.min(buffer.remaining(), MAX_LINE_LENGTH); i++) {
            if (buffer.get() == '\n') {
                newLinePos = i;
                break;
            }
        }
        buffer.reset();
        return newLinePos == -1 ? -1 : startPosition + newLinePos + 1;
    }

    public void closeChannel() throws IOException {
        if (fileChannel != null) {
            fileChannel.close();
            fileChannel = null;
        }
    }

    public long getFileSize() throws IOException {
        if (fileChannel == null) {
            throw new IllegalStateException("FileChannel is not open");
        }
        return fileChannel.size();
    }
}

