                    return Arrays.asList(response.body().string().split("\\n"));
                }
            } catch (Exception e) {
                exception = e;
                try {
                    Thread.sleep(100);
                } catch (InterruptedException ignored) {
                }
            }
        }
        if (exception != null) {
            exception.printStackTrace();
        }
        return null;
    }
}

