import okhttp3.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.TimeUnit;

public class Scraper {
    private OkHttpClient client;

    public Scraper() {
        this.client = new OkHttpClient.Builder()
                .connectTimeout(30, TimeUnit.SECONDS)
                .readTimeout(30, TimeUnit.SECONDS)
                .writeTimeout(30, TimeUnit.SECONDS)
                .build();
    }

    public List<String> scrape(String url, long timeoutMillis) throws IOException {
        return scrape(url, null, null, timeoutMillis);
    }

    public List<String> scrape(String url, String user, String password, long timeoutMillis) throws IOException {
        OkHttpClient clientWithTimeout = client.newBuilder()
                .connectTimeout(timeoutMillis, TimeUnit.MILLISECONDS)
                .readTimeout(timeoutMillis, TimeUnit.MILLISECONDS)
                .writeTimeout(timeoutMillis, TimeUnit.MILLISECONDS)
                .build();

        Request.Builder requestBuilder = new Request.Builder().url(url);
        if (user != null && password != null) {
            String credentials = Credentials.basic(user, password);
            requestBuilder.header("Authorization", credentials);
        }

        Request request = requestBuilder.build();
        List<String> retries = new ArrayList<>();
        int maxRetries = 3;
        int retryCount = 0;

        while (retryCount < maxRetries) {
            try (Response response = clientWithTimeout.newCall(request).execute()) {
                if (response.isSuccessful()) {
                    List<String> result = new ArrayList<>();
                    result.add(response.body().string());
                    return result;
                } else {
                    retries.add("Attempt " + (retryCount + 1) + ": Failed with status " + response.code());
                    retryCount++;
                }
            } catch (IOException e) {
                retries.add("Attempt " + (retryCount + 1) + ": " + e.getMessage());
                retryCount++;
            }
        }
        return retries;
    }
}

