public class KafkaItemReader<K, V> implements ItemReader<V>, ItemStream {
    private static final String TOPIC_PARTITION_OFFSETS = "topicPartitionOffsets";
    private static final long DEFAULT_POLL_TIMEOUT = 30000L;

    private List<TopicPartition> topicPartitions;
    private Map<TopicPartition, Long> partitionOffsets;
    private KafkaConsumer<K, V> kafkaConsumer;
    private Properties consumerProperties;
    private Iterator<ConsumerRecord<K, V>> consumerRecords;
    private Duration pollTimeout;
    private boolean saveState = true;

    public KafkaItemReader(Properties properties, String topic, Integer... partitions) {
        this(properties, topic, Arrays.asList(partitions));
    }

    public KafkaItemReader(Properties properties, String topic, List<Integer> partitions) {
        this.consumerProperties = properties;
        this.topicPartitions = partitions.stream()
                .map(p -> new TopicPartition(topic, p))
                .collect(Collectors.toList());
        this.pollTimeout = Duration.ofMillis(DEFAULT_POLL_TIMEOUT);
        this.partitionOffsets = new HashMap<>();
    }

    @Override
    public void open(ExecutionContext executionContext) throws ItemStreamException {
        this.kafkaConsumer = new KafkaConsumer<>(consumerProperties);
        kafkaConsumer.assign(topicPartitions);

        if (executionContext.containsKey(TOPIC_PARTITION_OFFSETS)) {
            Map<TopicPartition, Long> savedOffsets = (Map<TopicPartition, Long>) executionContext.get(TOPIC_PARTITION_OFFSETS);
            savedOffsets.forEach((tp, offset) -> {
                partitionOffsets.put(tp, offset);
                kafkaConsumer.seek(tp, offset);
            });
        } else {
            topicPartitions.forEach(tp -> {
                long offset = kafkaConsumer.position(tp);
                partitionOffsets.put(tp, offset);
            });
        }
    }

    @Override
    public V read() {
        if (consumerRecords == null || !consumerRecords.hasNext()) {
            ConsumerRecords<K, V> records = kafkaConsumer.poll(pollTimeout);
            if (records.isEmpty()) {
                return null;
            }
            consumerRecords = records.iterator();
        }
        ConsumerRecord<K, V> record = consumerRecords.next();
        partitionOffsets.put(new TopicPartition(record.topic(), record.partition()), record.offset() + 1);
        return record.value();
    }

    @Override
    public void update(ExecutionContext executionContext) throws ItemStreamException {
        if (saveState) {
            executionContext.put(TOPIC_PARTITION_OFFSETS, new HashMap<>(partitionOffsets));
        }
        kafkaConsumer.commitSync();
    }

    @Override
    public void close() throws ItemStreamException {
        if (kafkaConsumer != null) {
            kafkaConsumer.close();
        }
    }

    public void setPollTimeout(Duration pollTimeout) {
        this.pollTimeout = pollTimeout;
    }

    public void setSaveState(boolean saveState) {
        this.saveState = saveState;
    }
}

