import org.apache.kafka.clients.consumer.KafkaConsumer;
import org.apache.kafka.common.serialization.LongDeserializer;
import org.apache.kafka.common.serialization.StringDeserializer;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Component;

import javax.annotation.PostConstruct;
import javax.annotation.PreDestroy;
import java.time.Duration;
import java.util.Collections;
import java.util.Properties;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

@Component
public class KafkaConsumeOrderWorkaround {
    @Value("${kafka.bootstrap.servers}")
    private String bootstrapServers;
    @Value("${kafka.topic.order}")
    private String topicName;
    private String consumerGroup = "order";
    private Consumer<Long, String> consumer;
    private long pollingInterval = 250;
    private volatile boolean running = false;
    private Thread consumerThread;

    @PostConstruct
    public void startConsuming() {
        Properties props = new Properties();
        props.put(ConsumerConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapServers);
        props.put(ConsumerConfig.GROUP_ID_CONFIG, consumerGroup);
        props.put(ConsumerConfig.KEY_DESERIALIZER_CLASS_CONFIG, LongDeserializer.class.getName());
        props.put(ConsumerConfig.VALUE_DESERIALIZER_CLASS_CONFIG, StringDeserializer.class.getName());
        props.put(ConsumerConfig.ENABLE_AUTO_COMMIT_CONFIG, "false");
        props.put(ConsumerConfig.AUTO_OFFSET_RESET_CONFIG, "earliest");

        consumer = new KafkaConsumer<>(props);
        consumer.subscribe(Collections.singletonList(topicName));

        running = true;
        consumerThread = new Thread(this::consumeMessages);
        consumerThread.start();
    }

    private void consumeMessages() {
        try {
            while (running) {
                ConsumerRecords<Long, String> records = consumer.poll(Duration.ofMillis(pollingInterval));
                if (!records.isEmpty()) {
                    records.forEach(record -> {
                        try {
                            processMessage(record.value());
                        } catch (Exception e) {
                            System.err.println("Error processing message: " + e.getMessage());
                        }
                    });
                    consumer.commitAsync();
                }
            }
        } finally {
            consumer.close();
        }
    }

    private void processMessage(String message) {
        System.out.println("Processing message: " + message);
    }

    @PreDestroy
    public void stopConsuming() {
        running = false;
        if (consumerThread != null) {
            try {
                consumerThread.join();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        }
    }
}

