public class DlqAction implements RuleAction {
    private static final Logger log = LoggerFactory.getLogger(DlqAction.class);
    public static final String TYPE = "DLQ";
    public static final String DLQ_TOPIC = "dlq.topic";
    public static final String DLQ_AUTO_FLUSH = "dlq.auto.flush";
    public static final String PRODUCER = "producer";
    public static final String HEADER_PREFIX = "rule.";
    public static final String RULE_NAME = HEADER_PREFIX + "name";
    public static final String RULE_MODE = HEADER_PREFIX + "mode";
    public static final String RULE_SUBJECT = HEADER_PREFIX + "subject";
    public static final String RULE_TOPIC = HEADER_PREFIX + "topic";
    public static final String RULE_EXCEPTION = HEADER_PREFIX + "exception";

    private Map<String, ?> configs;
    private String topic;
    private boolean autoFlush;
    private KafkaProducer<byte[], byte[]> producer;

    @Override
    public void configure(Map<String, ?> configs) {
        this.configs = configs;
        this.topic = (String) configs.get(DLQ_TOPIC);
        this.autoFlush = Boolean.parseBoolean((String) configs.getOrDefault(DLQ_AUTO_FLUSH, "false"));
    }

    @Override
    public void run(RuleContext ctx, Object message, RuleException ex) {
        try {
            byte[] value = convertToBytes(ctx, message);
            ProducerRecord<byte[], byte[]> record = new ProducerRecord<>(topic, value);
            populateHeaders(ctx, record, ex);
            producer().send(record, (metadata, exception) -> {
                if (exception != null) {
                    log.error("Failed to send message to DLQ topic {}", topic, exception);
                }
            });
            if (autoFlush) {
                producer().flush();
            }
        } catch (Exception e) {
            log.error("Error processing DLQ message", e);
        }
    }

    private KafkaProducer<byte[], byte[]> producer() {
        if (producer == null) {
            Map<String, Object> producerConfigs = baseProducerConfigs();
            if (configs.containsKey(PRODUCER)) {
                producerConfigs.putAll((Map<String, Object>) configs.get(PRODUCER));
            }
            producer = new KafkaProducer<>(producerConfigs, new ByteArraySerializer(), new ByteArraySerializer());
        }
        return producer;
    }

    private byte[] convertToBytes(RuleContext ctx, Object message) {
        if (message instanceof byte[]) {
            return (byte[]) message;
        } else if (message instanceof String) {
            return toBytes((String) message);
        } else {
            return convertToJsonBytes(ctx, message);
        }
    }

    private byte[] convertToJsonBytes(RuleContext ctx, Object message) {
        try {
            ObjectMapper mapper = new ObjectMapper();
            return mapper.writeValueAsBytes(message);
        } catch (Exception e) {
            log.error("Failed to serialize message to JSON", e);
            return new byte[0];
        }
    }

    private void populateHeaders(RuleContext ctx, ProducerRecord<byte[], byte[]> producerRecord, RuleException ex) {
        List<Header> headers = new ArrayList<>();
        headers.add(new RecordHeader(RULE_NAME, toBytes(ctx.getRuleName())));
        headers.add(new RecordHeader(RULE_MODE, toBytes(ctx.getRuleMode())));
        headers.add(new RecordHeader(RULE_SUBJECT, toBytes(ctx.getSubject())));
        headers.add(new RecordHeader(RULE_TOPIC, toBytes(ctx.getTopic())));
        if (ex != null) {
            headers.add(new RecordHeader(RULE_EXCEPTION, toBytes(ex.getMessage())));
        }
        producerRecord.headers().addAll(headers);
    }

    private byte[] toBytes(String value) {
        return value != null ? value.getBytes(StandardCharsets.UTF_8) : new byte[0];
    }

    private Map<String, Object> baseProducerConfigs() {
        Map<String, Object> configs = new HashMap<>();
        configs.put("key.serializer", ByteArraySerializer.class.getName());
        configs.put("value.serializer", ByteArraySerializer.class.getName());
        configs.put("acks", "all");
        configs.put("retries", 3);
        return configs;
    }

    @Override
    public void close() {
        if (producer != null) {
            producer.close();
        }
    }
}

