import org.apache.kafka.clients.producer.KafkaProducer;
import org.apache.kafka.clients.producer.ProducerConfig;
import org.apache.kafka.common.serialization.ByteArraySerializer;
import org.slf4j.Logger;

public class KafkaSampleProducer {
    private static final Duration PRODUCER_CLOSE_TIMEOUT = Duration.ofSeconds(30);
    private volatile boolean _shutdown = false;
    private Producer<byte[], byte[]> _producer;

    public void createProducer(Map<String, ?> config, String producerClientId) {
        if (_producer != null) {
            throw new IllegalStateException("Producer already created");
        }
        Map<String, Object> producerConfig = new java.util.HashMap<>(config);
        producerConfig.put(ProducerConfig.CLIENT_ID_CONFIG, producerClientId);
        producerConfig.put(ProducerConfig.KEY_SERIALIZER_CLASS_CONFIG, ByteArraySerializer.class.getName());
        producerConfig.put(ProducerConfig.VALUE_SERIALIZER_CLASS_CONFIG, ByteArraySerializer.class.getName());
        _producer = new KafkaProducer<>(producerConfig);
    }

    public AtomicInteger storePartitionMetricSamples(MetricSampler.Samples samples, Producer<byte[], byte[]> producer, String partitionMetricSampleStoreTopic, Logger log) {
        AtomicInteger sentSamples = new AtomicInteger(0);
        if (samples == null || producer == null || partitionMetricSampleStoreTopic == null) {
            return sentSamples;
        }
        samples.forEach(sample -> {
            if (_shutdown) {
                return;
            }
            try {
                ProducerRecord<byte[], byte[]> record = new ProducerRecord<>(partitionMetricSampleStoreTopic, sample.toByteArray());
                producer.send(record, (metadata, exception) -> {
                    if (exception != null) {
                        log.error("Failed to send partition metric sample", exception);
                    } else {
                        sentSamples.incrementAndGet();
                    }
                });
            } catch (Exception e) {
                log.error("Error sending partition metric sample", e);
            }
        });
        return sentSamples;
    }

    public void close() {
        _shutdown = true;
        if (_producer != null) {
            _producer.close(PRODUCER_CLOSE_TIMEOUT);
            _producer = null;
        }
    }
}

