        this.size = fileChannel.size();
        this.mappedByteBuffer = fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, size);
        this.unmapper = getUnmapper(fileChannel);
    }

    public void close() throws IOException {
        if (fileChannel != null) {
            try {
                fileChannel.close();
            } finally {
                fileChannel = null;
                if (mappedByteBuffer != null) {
                    unmapper.accept(mappedByteBuffer);
                    mappedByteBuffer = null;
                }
            }
        }
    }

    public void seek(long position) throws IOException {
        checkClosed();
        if (position < 0 || position > size) {
            throw new IOException("Invalid position");
        }
        mappedByteBuffer.position((int) position);
    }

    public long getPosition() throws IOException {
        checkClosed();
        return mappedByteBuffer.position();
    }

    public int read() throws IOException {
        checkClosed();
        if (!mappedByteBuffer.hasRemaining()) {
            return -1;
        }
        return mappedByteBuffer.get() & 0xFF;
    }

