    private static Consumer<? super ByteBuffer> getUnmapper(FileChannel channel) {
        return buffer -> {
            try {
                Class<?> unsafeClass = Class.forName("sun.misc.Unsafe");
                java.lang.reflect.Field theUnsafe = unsafeClass.getDeclaredField("theUnsafe");
                theUnsafe.setAccessible(true);
                Object unsafe = theUnsafe.get(null);
                java.lang.reflect.Method invokeCleaner = unsafeClass.getMethod("invokeCleaner", ByteBuffer.class);
                invokeCleaner.invoke(unsafe, buffer);
            } catch (Exception e) {
                throw new RuntimeException("Failed to unmap buffer", e);
            }
        };
