        Thread outThread = new Thread(() -> copyStream(process.getInputStream(), output, context.getLogger(), getCharset()));
        Thread errThread = new Thread(() -> copyStream(process.getErrorStream(), error, context.getLogger(), getCharset()));

        outThread.start();
        errThread.start();

        int exitCode = process.waitFor();
        outThread.join();
        errThread.join();

        return new ProcessResult(exitCode, output.toString(), error.toString());
    }

    protected void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
        try {
            byte[] buffer = new byte[1024];
            int bytesRead;
            while ((bytesRead = is.read(buffer)) != -1) {
                String content = new String(buffer, 0, bytesRead, charset);
                sb.append(content);
                omsLogger.info(content);
            }
