import java.io.IOException;
import java.io.OutputStream;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.OpenOption;
import java.nio.file.StandardOpenOption;

public class FileChannelUtils {
    public static OutputStream newOutputStream(FileChannel channel, boolean forceAtClose) {
        return new OutputStream() {
            @Override
            public void write(int b) throws IOException {
                byte[] buffer = new byte[]{(byte) b};
                write(buffer, 0, 1);
            }

            @Override
            public void write(byte[] b, int off, int len) throws IOException {
                while (len > 0) {
                    int written = channel.write(java.nio.ByteBuffer.wrap(b, off, len));
                    off += written;
                    len -= written;
                }
            }

            @Override
            public void close() throws IOException {
                try {
                    if (forceAtClose) {
                        channel.force(true);
                    }
                } finally {
                    channel.close();
                }
            }
        };
    }

    public static OutputStream newOutputStreamForceAtClose(Path p, OpenOption... options) throws IOException {
        OpenOption[] newOptions = new OpenOption[options.length + 1];
        System.arraycopy(options, 0, newOptions, 0, options.length);
        newOptions[options.length] = StandardOpenOption.WRITE;
        FileChannel channel = FileChannel.open(p, newOptions);
        return newOutputStream(channel, true);
    }

    public static FileChannel newFileChannel(Path p, OpenOption... options) throws IOException {
        return FileChannel.open(p, options);
    }
