public class ShellCommand extends AbstractCommand {
    private String command;
    private List<String> argv;
    private static final Pattern shellWildcardPattern = Pattern.compile("[*?$><|&;`'\"\\\\]");

    public ShellCommand(String command) {
        this.command = command;
        this.argv = hasWildcard(command) 
            ? Arrays.asList("/bin/sh", "-c", command) 
            : tokenize(command);
    }

    private boolean hasWildcard(String command) {
        return shellWildcardPattern.matcher(command).find();
    }

    private List<String> tokenize(String s) {
        List<String> tokens = new ArrayList<>();
        boolean inQuotes = false;
        StringBuilder currentToken = new StringBuilder();
        for (char c : s.toCharArray()) {
            if (c == '"') {
                inQuotes = !inQuotes;
            } else if (Character.isWhitespace(c) && !inQuotes) {
                if (currentToken.length() > 0) {
                    tokens.add(currentToken.toString());
                    currentToken = new StringBuilder();
                }
            } else {
                currentToken.append(c);
            }
        }
        if (currentToken.length() > 0) {
            tokens.add(currentToken.toString());
        }
        return tokens;
    }

    @Override
    public void doExecute(ConcurrentTestCommandExecutor executor) {
        ProcessBuilder pb = new ProcessBuilder(argv);
        pb.redirectErrorStream(true);
        try {
            Process process = pb.start();
            InputStream inputStream = process.getInputStream();
            BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));
            String line;
