        }
    }

    public boolean waitFor() {
        return waitFor(-1, -1);
    }

    public boolean waitFor(int timeout) {
        return waitFor(timeout, -1);
    }

    private boolean waitFor(int timeout, int index) {
        synchronized (mLock) {
            long startTime = System.currentTimeMillis();
            while (mCounter.size() > (index == -1 ? 0 : index)) {
                try {
                    if (timeout > 0 && (System.currentTimeMillis() - startTime) >= timeout) {
                        return false;
                    }
                    mLock.wait(100);
                } catch (InterruptedException e) {
                    return false;
                }
            }
            return true;
        }
    }

    public boolean isActive() {
        return mIsActive;
    }

    public boolean isRunning() {
        return mCounter.size() > 0;
    }

    public boolean isRoot() {
        return mIsRoot;
    }

    public void destroy() {
        mIsActive = false;
        if (mConnection != null) {
            mConnection.destroy();
        }
        if (mStdInput != null) {
            try {
                mStdInput.close();
            } catch (IOException ignored) {}
        }
        if (mStdOutput != null) {
            try {
                mStdOutput.close();
            } catch (IOException ignored) {}
        }
        if (mStdOutputWorker != null) {
            mStdOutputWorker.interrupt();
        }
    }

    public interface OnStreamListener {
        void onStreamStart();
        void onStreamInput(String input);
        void onStreamStop(int exitCode);
        void onStreamDied();
    }

    private static class Counter {
        private int count;

        public synchronized int size() {
            return count;
        }

        public synchronized int encrease() {
            return ++count;
        }

        public synchronized int decrease() {
            return --count;
        }

        public synchronized void reset() {
            count = 0;
        }
    }
}

