                    String[] parts = line.split("=", 2);
                    if (parts.length == 2) {
                        env.put(parts[0], parts[1]);
                    }
                }
            } catch (IOException e) {
                Logger.getLogger("JRE").println("Failed to read custom_env.txt: " + e.getMessage());
            }
        }

        String libPath = jreHome + NATIVE_LIB_DIR;
        String path = env.get("PATH") != null ? env.get("PATH") : "";
