            pb = new ProcessBuilder(command);
        } else {
            String[] cmdLine = new String[command.length];
            for (int i = 0; i < command.length; i++)
                cmdLine[i] = command[i].replace("\"", "\\\"");
            pb = new ProcessBuilder(cmdLine);
            Map<String, String> env = pb.environment();
            env.put("CYGWIN", "nodosfilewarning");
        }
        pb.redirectErrorStream(true);

        return pb.start();
    }

    public int listenOnProcess(String[] arguments, ProcessOutputHandler handler) {
        try {
            final Process p = execRedirected(arguments);
            Thread thread = new Thread() {
                public void run() {
                    try {
                        InputStreamReader reader = new InputStreamReader(p.getInputStream());
                        int c;
                        String line = "";
                        while ((c = reader.read()) != -1) {
                            if ((char) c == '\r')
                                continue;
                            if ((char) c == '\n') {
                                handler.handleOutputLine(line);
                                line = "";
                            } else {
                                line += (char) c;
                            }
                        }
                        handler.handleOutputLine(line);
                        reader.close();

                        reader = new InputStreamReader(p.getErrorStream());
                        while ((c = reader.read()) != -1)
                            System.err.print((char) c);
                        reader.close();
                    } catch (Exception e) {
                        handler.handleError("Run Exception: " + e.getMessage());
                    }
                }
            };
            thread.start();
            int res = p.waitFor();
            thread.join();
            return res;
        } catch (Exception e) {
            handler.handleError("Process Exception: " + e.getMessage());
        }
        return -1;
    }

